mapper.update:
update optional_course
set grade=#{grade}
where no = #{no}

mapper.select:
select no, optional_course.studentNo, student.studentName, optional_course.courseNo, course.courseName, grade from optional_course,student,course where optional_course.studentNo = student.studentNo and optional_course.courseNo = course.courseNo
and student.studentNo=#{studentNo}
and student.studentName like "%"#{studentName}"%"
and course.courseNo=#{courseNo}
and course.courseName like "%"#{courseName}"%"
