mapper.update:
update student
set studentName=#{studentName},
    studentSex=#{studentSex},
    studentAge=#{studentAge},
    studentClassNo=#{classNo}
where studentNo = #{studentNo}

mapper.delete:
delete
from student
where studentNo = #{studentNo};

delete
from login
where userName = #{studentNo}

mapper.select:
select studentNo,
studentName,
studentSex,
studentAge,
studentClassNo AS classNo,
className,
majorNo,
majorName,
facultyNo,
facultyName
from student,
faculty,
class,
major
where student.studentClassNo = class.classNo
and class.classMajorNo = major.majorNo
and major.majorFacultiesNo = faculty.facultyNo

    and studentNo=#{studentNo}


    and studentName like "%"#{studentName}"%"


    and studentSex=#{studentSex}


    and studentAge=#{studentAge}


    and class.classNo=#{classNo}


    and class.className=#{className}


    and major.majorNo=#{majorNo}


    and major.majorName=#{majorName}


    and faculty.facultyNo=#{facultyNo}


    and faculty.facultyName=#{facultyName}

mapper.insert:
select if(max(studentNo) is null, 100001, max(studentNo) + 1) as newId from student
insert into student (studentNo,studentName,studentSex,studentAge,studentClassNo) value(#{studentNo},#{studentName},#{studentSex},#{studentAge},#{classNo});
insert into login (userName, userPassword, permissions) value (#{studentNo},"123456",2)
