mapper.select:
SELECT *
FROM login
WHERE userName = #{userName}
  AND userPassword = #{userPassword}

mapper.update:
update login
set userPassword = #{newPassword}
where userName = #{userName}

update login
set avatarName = #{avatarName}
where userName = #{userName}

mapper.select:
SELECT * FROM login
and userName like "%"#{userName}"%"
and permissions=#{permissions}

mapper.update:
update login
set userPassword="123456"
where userName = #{userName}
